const fetch = require('node-fetch');
module.exports = {
  get: async (url, opts) => {
    const res = await fetch(url, opts);
    return { body: await res.text(), status: res.status, headers: res.headers };
  },
  post: async (url, body, opts) => {
    const res = await fetch(url, Object.assign({ method: 'POST', body }, opts));
    return { body: await res.text(), status: res.status, headers: res.headers };
  }
};
